function updateDom(a){var b=$("#message-container");b.empty(),$.each(a,function(a,c){b.append("<p>"+(a+1)+": "+c.name+": "+c.message+"</p>")})}function getData(){$.ajax({type:"GET",url:"/messages",success:function(a){updateDom(a)}})}$(document).ready(function(){getData(),$("#inputForm").submit(function(a){a.preventDefault();var b=$("input, textarea").serialize();$.ajax({type:"POST",url:"/messages",data:b,success:function(){getData()}})}),$("#refresh-icon").on("click",function(){getData()})});