var currentStudent=1,studentAmount,displayStudent=function(a){$(".name, .description, .thanks").empty(),$(".name").html("<p>"+a.name+"</p>"),$(".description").html("<p>"+a.desc+"</p>"),$(".thanks").html("<p>"+a.thanks+"</p>")},queryStudent=function(a){$.get("query/"+a,function(a){displayStudent(a)})},queryStudentCount=function(){$.get("query/count",function(a){studentAmount=a.count}).then(function(){for(i=1;i<=studentAmount;i++)$(".pagination").append('<li><a href="#">'+i+"</a></li>")})};queryStudent(1),queryStudentCount(),$(document).ready(function(){$(".pagination").on("click","li",function(){queryStudent($(this).text())}),$(".next").on("click",function(){studentAmount>currentStudent?currentStudent++:currentStudent=1,queryStudent(currentStudent)}),$(".previous").on("click",function(){currentStudent>1?currentStudent--:currentStudent=studentAmount,queryStudent(currentStudent)})});