var myApp=angular.module("taskApp",[]);myApp.controller("taskController",["$scope","$http",function(a,b){a.heading="Angular Task Application",a.updateTasks=function(){b.get("/tasks").success(function(b){a.tasks=b})},a.sendTask=function(){b.post("/tasks/create",{task:a.taskInput}).success(function(b){a.tasks.unshift(b)})},a.deleteTask=function(c){b["delete"]("/tasks/"+c+"/delete").success(function(){a.updateTasks()})},a.updateTaskStatus=function(c,d){b.put("/tasks/"+c+"/swap-status",{completed:d}).success(function(){a.updateTasks()})},a.updateTasks()}]);