var studentNum=1,studentCount,getStudent=function(a){$.get("query/"+a,function(a){$(".data").empty(),$(".data").append("<p>"+a.name+"</p>"),$(".data").append("<p>"+a.desc+"</p>"),$(".data").append("<p>"+a.thanks+"</p>")})};$(document).ready(function(){$.get("query/count",function(a){studentCount=a.count}),$(".next").on("click",function(){studentCount>studentNum?studentNum++:studentNum=1,getStudent(studentNum)}),$(".previous").on("click",function(){studentNum>1?studentNum--:studentNum=studentCount,getStudent(studentNum)}),getStudent(1)});